/* 반올림할 때 ROUND 함수를 사용한다, 3째자리 반올림이면 -> 2로 표현 */
SELECT RI.REST_ID, RI.REST_NAME, RI.FOOD_TYPE, RI.FAVORITES, RI.ADDRESS, ROUND(AVG(RR.REVIEW_SCORE), 2) AS SCORE FROM REST_INFO AS RI JOIN REST_REVIEW AS RR ON RI.REST_ID = RR.REST_ID WHERE ADDRESS LIKE '서울%' GROUP BY RI.REST_ID ORDER BY SCORE DESC, RI.FAVORITES DESC;